// Extract product description and create 360째 video prompt
const description = $json.choices[0].message.content;
const productName = $('Webhook - Upload Image').item.json.body.product_name || 'product';
const imageBase64 = $('Webhook - Upload Image').item.json.body.image_base64;
const projectId = $env.VERTEX_AI_PROJECT_ID || 'your-project-id';

// Create optimized 360째 rotation prompt for Vertex AI Veo2
const videoPrompt = `Create a smooth 360-degree rotation video of this ${productName}. ${description}. 
Style: Professional product showcase, clean white background, studio lighting, smooth rotation clockwise.
Camera: Fixed position, product rotates smoothly in center frame.
Duration: 8 seconds, seamless loop, high quality 720p.
Lighting: Even, professional studio lighting highlighting product features.
Focus: Keep product in sharp focus throughout rotation.`;

// Clean base64 image data
const cleanBase64 = imageBase64.includes(',') ? imageBase64.split(',')[1] : imageBase64;

return {
  video_prompt: videoPrompt,
  product_name: productName,
  image_base64: cleanBase64,
  description: description,
  project_id: projectId
};

// Extract video data from Vertex AI Veo2 response
const response = $json;

if (response.predictions && response.predictions[0]) {
  const prediction = response.predictions[0];

  // Check if video data is available
  if (prediction.videoBytes) {
    // Direct video bytes
    const videoBuffer = Buffer.from(prediction.videoBytes, 'base64');

    return {
      video_data: videoBuffer,
      filename: `360_video_${Date.now()}.mp4`,
      size: videoBuffer.length,
      mimeType: 'video/mp4',
      success: true
    };
  } else if (prediction.videoUri) {
    // Video stored in Cloud Storage
    return {
      video_url: prediction.videoUri,
      filename: `360_video_${Date.now()}.mp4`,
      success: true,
      message: 'Video generated successfully and stored in Cloud Storage'
    };
  } else if (prediction.status === 'PROCESSING' || prediction.status === 'PENDING') {
    // Video still processing
    return {
      success: false,
      processing: true,
      status: prediction.status,
      message: 'Video is still being generated. Please wait...'
    };
  }
}

// If no valid response
throw new Error('No video data found in Vertex AI Veo2 response: ' + JSON.stringify(response));

// Error handling - return error message
const errorMsg = {
  error: 'Video generation failed',
  message: 'Unable to generate 360째 video with Vertex AI Veo2. Please check your configuration and try again.',
  timestamp: new Date().toISOString(),
  details: 'GLM-4.5 processing or Vertex AI Veo2 generation failed',
  troubleshooting: {
    'Check environment variables': [
      'VERTEX_AI_PROJECT_ID - Your Google Cloud Project ID',
      'VERTEX_AI_ACCESS_TOKEN - Valid access token or service account key',
      'GLM_API_KEY - Your GLM API key'
    ],
    'Vertex AI Requirements': [
      'Ensure Vertex AI API is enabled in your project',
      'Veo2 model access must be granted',
      'Service account must have aiplatform.endpoints.predict permission'
    ]
  }
};

return errorMsg;
